const assert = require("assert");

describe("Testing Attacher", () => {
    it('Executes once', done => {
        const pipeline = require('../index.js');

        pipeline().once((() => {
            done();
        }))();
    });

    it('Executes pre', done => {
        const pipeline = require('../index.js');
        pipeline().pre((() => {done();}))();
    });

    it('Executes post', done => {
        const pipeline = require('../index.js');
        pipeline().post((() => {done();}))();
    });
});